import { useState } from 'react'
import './Form.css';

const Form = () => {
    const [checked, setChecked] = useState({
        "Action": false,
        "Racing / Driving": false,
    })

    const [range, setRange] = useState([0, 99])

    const printChecked = () => {
        Object.keys(checked).forEach((key) => console.log(key, checked[key]))
    }

    const printRange = () => {
        console.log(range[0], range[1])
    }

    const toggleChecked = (genre) => {
        setChecked({...checked, [genre]: !checked[genre]})
    }

    const editRange = (pos, val) => {
        let newArr = [...range]
        if(val === "")
            newArr[pos] = (pos === 0)? 0 : 99
        else
            newArr[pos] = parseInt(val)
        setRange(newArr)
    }

    return (
        <div className="Form">
            <div className="Title">
                <span>Welcome to GameREC!</span>
            </div>

            <div>
                <div className="Category">
                    <label className="Label">Select Your Favorite Genres</label>

                    <div className="Checkbox-Container">
                        <label>
                            <input type="checkbox"
                                defaultChecked={false}
                                onChange={() => toggleChecked("Action")}
                            />
                            Action
                        </label>
                        <label>
                            <input type="checkbox"
                                defaultChecked={false}
                                onChange={() => toggleChecked("Racing / Driving")}
                            />
                            Racing / Driving
                        </label>
                    </div>
                </div>

                <div className="Category">
                    <label className="Label">Give a Price Range</label>
                    <div className="Price-Range">
                        <div>
                            <input 
                                type="number"
                                className="Rangebox" 
                                placeholder="1"
                                onChange={(e) => editRange(0, e.target.value)}
                            />
                        </div>
                        <div>
                            <input 
                                type="number" 
                                className="Rangebox" 
                                placeholder="99" 
                                onChange={(e) => editRange(1, e.target.value)}
                            />
                        </div>
                    </div>
                </div>       

            </div>

            <div className="Submit">Get Recommendations!</div>
        </div>
    )
}

export default Form;
