import { useState } from 'react'
import Recommendations from './Recommendations'
import './Form.css';

const Form = () => {
    const [checked, setChecked] = useState({
        "Action": false,
        "Racing / Driving": false,
        "Strategy": false,
    })
    const [priceRange, setPriceRange] = useState([0, 99])
    const [timeRange, setTimeRange] = useState([0, 999])
    const [online, setOnline] = useState(2)

    const [submitted, setSubmitted] = useState(false)
    const [responseData, setResponseData] = useState([])

    const toggleChecked = (genre) => {
        setChecked({...checked, [genre]: !checked[genre]})
    }

    const editPriceRange = (pos, val) => {
        let newArr = [...priceRange]
        if(val === "")
            newArr[pos] = (pos === 0)? 0 : 99
        else
            newArr[pos] = parseInt(val)
        setPriceRange(newArr)
    }

    const editTimeRange = (pos, val) => {
        let newArr = [...timeRange]
        if(val === "")
            newArr[pos] = (pos === 0)? 0 : 999
        else
            newArr[pos] = parseInt(val)
        setTimeRange(newArr)
    }

    const selectedButton = {
        backgroundColor: "#34aeeb",
        color: "white"
    }

    const nonSelectedButton = {
        backgroundColor: "white"
    }

    const submitHandler = async () => {
        try {
            // Convert checked boxes as a list of genres
            const genres = []
            Object.entries(checked).forEach(([key, value]) => {
                if(value === true) genres.push(key)
            })
            // Set up payload
            const payload = {
                genres: genres,
                priceRange: priceRange,
                timeRange: timeRange,
                online: online,
            }
            // Request recommendations
            const req = await fetch("http://127.0.0.1:8000/rec", {
                method: "POST",
                header: {'Content-Type': 'application/json'},
                body: JSON.stringify(payload)
            })
            // Response as 15 returned video games
            const res = await req.json()
            // Direct user to suggestions
            console.log(res)
            setSubmitted(true)
            setResponseData(res)
            console.log(responseData)

        } catch(error) {
            console.error(error)
        }
    }

    return (
        <div className="Form">
            <div className="Title">
                <span>Welcome to GameREC!</span>
            </div>

            {submitted? 
                <Recommendations gameArr={responseData} />
                :
                <>
                <div>
                    <div className="Category">
                        <label className="Label">Select Your Favorite Genres</label>

                        <div className="Checkbox-Container">
                            <label>
                                <input type="checkbox"
                                    defaultChecked={false}
                                    onChange={() => toggleChecked("Action")}
                                />
                                Action
                            </label>
                            <label>
                                <input type="checkbox"
                                    defaultChecked={false}
                                    onChange={() => toggleChecked("Racing / Driving")}
                                />
                                Racing / Driving
                            </label>
                            <label>
                                <input type="checkbox"
                                    defaultChecked={false}
                                    onChange={() => toggleChecked("Strategy")}
                                />
                                Strategy
                            </label>
                        </div>
                    </div>

                    <div className="Category">
                        <label className="Label">Give a Price Range</label>
                        <div className="Price-Range">
                            <div>
                                <input 
                                    type="number"
                                    className="Rangebox" 
                                    placeholder="0"
                                    val={priceRange[0]}
                                    min="0"
                                    max="99"
                                    onChange={(e) => editPriceRange(0, e.target.value)}
                                />
                            </div>
                            <div>
                                <input 
                                    type="number" 
                                    className="Rangebox" 
                                    placeholder="99"
                                    val={priceRange[1]}
                                    min="0"
                                    max="99"
                                    onChange={(e) => editPriceRange(1, e.target.value)}
                                />
                            </div>
                        </div>
                    </div>       

                    <div className="Category">
                        <label className="Label">Give a Price Range</label>
                        <div className="Price-Range">
                            <div>
                                <input 
                                    type="number"
                                    className="Rangebox" 
                                    placeholder="0"
                                    val={timeRange[0]}
                                    min="0"
                                    max="999"
                                    onChange={(e) => editTimeRange(0, e.target.value)}
                                />
                            </div>
                            <div>
                                <input 
                                    type="number" 
                                    className="Rangebox" 
                                    placeholder="999"
                                    val={timeRange[1]}
                                    min="0"
                                    max="999"
                                    onChange={(e) => editTimeRange(1, e.target.value)}
                                />
                            </div>
                        </div>
                    </div>

                    <div className="Category">
                        <label className="Label">Online/Offline?</label>
                        <div className="Online-Button-Wrap">
                            <div 
                                className="Online-Button"
                                style={online === 2? selectedButton : nonSelectedButton}
                                onClick={() => setOnline(2)}
                            >
                                All
                            </div>
                            <div 
                                className="Online-Button"
                                style={online === 0? selectedButton : nonSelectedButton}
                                onClick={() => setOnline(0)}
                            >
                                Offline
                            </div>
                            <div 
                                className="Online-Button"
                                style={online === 1? selectedButton : nonSelectedButton}
                                onClick={() => setOnline(1)}
                            >
                                Online
                            </div>
                        </div>
                    </div>

                </div>

                <div className="Submit" onClick={submitHandler}>
                    Get Recommendations!
                </div>
                </>
            }
        </div>
    )
}

export default Form;
