import { useState } from 'react'
import './Form.css';

const Form = (props) => {
    const [checked, setChecked] = useState({})

    const test = () => {
        checked.forEach((key) => console.log(key + checked[key]))
    }

    return (
        <div className="Form">
            <div className="Title">
                <span>Welcome to GameREC!</span>
            </div>

            <div>
                <div className="Category">
                    <label className="Label">Select Your Favorite Genres</label>

                    <div className="Checkbox-Container">
                        <label>
                            <input type="checkbox"
                                defaultChecked={false}
                                onChange={() => setChecked({
                                    ...checked, "Action": true
                                })}
                            />
                            Action
                        </label>
                        <label>
                            <input type="checkbox"
                                defaultChecked={false}
                                onChange={() => setChecked({
                                    ...checked, "Racing / Driving": true
                                })}
                            />
                            Racing / Driving
                        </label>
                    </div>
                </div>

                <div className="Category">
                    <label className="Label">Give a Price Range</label>
                    <div className="Price-Range">
                        <div>
                            <input type="number" className="Rangebox" placeholder="0" id="lower-range" />
                        </div>
                        <div>
                            <input type="number" className="Rangebox" placeholder='99' id="upper-range" />
                        </div>
                    </div>
                </div>

                <button>Test</button>
            </div>
        </div>
    )
}

export default Form;
